nohup java -Xmx4g -jar $GOWINDA --snp-file $TOTAL --candidate-snp-file $SIGN --gene-set-file $KEGG --annotation-file $GTF_KEGG --simulations 100000 --min-significance 1 --gene-definition upstream1000 --threads 2 --output-file `basename $SIGN .cmhout`"_upstream1000_snp_KEGG.gowinda" --mode snp --min-genes 1 2>&1 >>  `basename $SIGN .cmhout`"_upstream1000_snp_KEGG.gowinda".out &
